import logo from './logo.svg'
import './App.css'
import './header.scss'
import TestJs from './App.test'
import TextForm from './text'
import InputHeader from './input_header'
import HeaderModal from './header_modal'
import { useState } from 'react'



function App() {
  const  [text, setText] = useState('')
  const  [todoList, setTodoList] = useState([{id:1, text: "Снимай видео"}, {id:2, text: "Я приехал"}, {id:3, text: "До встречи"},])

  function addTodo() {
    setTodoList([...todoList, {id: Date.now(), text}])
  }
  return (
    <div className="App">
      <header className="App-header">

      </header>
      <div className="header-input">
        <input className="mx-input" onChange={e => setText(e.target.value)} value={text} type="text"/>
        <button onClick={() => addTodo()}>Добавить</button>
      </div>
        <ul>
          {todoList.map(({id, text}) =>
            <li>
              <input onClick={() => setTodoList([...todoList.filter(todo => todo.id !== id)])} type="checkbox"/>
              {id} {text}
            </li>

          )}
        </ul>

    </div>
  )
}
export default App
