import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();

var addAudit = function() {
    var elemKod = document.querySelector('.mx-section').innerHTML;
    var elemOutKod = document.querySelector('.mx-fake');

    elemOutKod.innerHTML += '<div>' + elemKod + '</div>';
};

document.onclick = function() {
    var elem = window.event.srcElement;
    if (elem.className === "mx-new-b") {
        addAudit();
    }
};

let myblock = document.querySelectorAll('.mx-main');

myblock.forEach(block => block.addEventListener('click', removeBlock));

function removeBlock() {
  let block = this;
  block.style.opacity = 1;
  let blockId = setInterval(function() {
    if (block.style.opacity > 0) block.style.opacity -= .1;
    else {
      clearInterval(blockId);
      block.remove();
    }
  }, 60)
}
