import React from 'react'

export default function moreBlock() {
  const { render } = ReactDOM;
  const { Component } = React;

  const api = {
    fetchItems (count) {
      const items = [
        {
          "title": "First Block",
          "imageURL": "https://storage.googleapis.com/gweb-uniblog-publish-prod/images/android_ambassador_v1_cmyk_200px.max-200x200.png",
          "border": "7px dotted yellow"
        },
        {
          "title": "Second Block",
          "imageURL": "https://www.cmcgroups.com/wp-content/uploads/2018/02/logo-att-color-trans-200x200.png",
          "border": "7px dashed yellow"
        },
        {
          "title": "Third Block",
          "imageURL": "https://21clradio.com/wp-content/uploads/2015/09/Blend-Space-Logo-1.png",
          "border": "7px solid yellow"
        }
      ]

      count = parseInt(count);

      if (count < 0 || count > items.length) {
        throw new Error(`InvalidArgumentError: ${count} is invalid count`);
      }

      return items.slice(0, count);
    }
  }

  const Popup = props => {
    const classList = ['modal'];

    if (props.isVisible) classList.push('is-active');

    return (
      <div className={classList.join(' ')}>
        <div className="modal-background"></div>
        <div className="modal-content">
          {props.children}
        </div>
        <button className="modal-close is-large" aria-label="close" onClick={props.onClose}></button>
      </div>
    )
  }

  class AddForm extends Component {
    state = {
      selectedCount: 1
    }

    handleChange = e => {
      this.setState({ selectedCount: parseInt(e.target.value) });
    }

    handleSubmit = e => {
      e.preventDefault();
      this.props.onSubmit(this.state.selectedCount);
      this.setState({ selectedCount: 1 });
    }

    handleCancel = e => {
      this.props.onCancel();
    }

    renderRadioButtons () {
      return new Array(this.props.total).fill(null).map((el, i) => {
        const value = i + 1;

        return (
          <label key={value} className='radio'>
            <input type='radio'
                   value={value}
                   checked={value === this.state.selectedCount}
                   onChange={this.handleChange}
            />
            {value}
          </label>
        )
      });
    }

    render () {
      return (
        <Popup isVisible={this.props.isVisible} onClose={this.handleCancel}>
          <form onSubmit={this.handleSubmit}>
            <div className='card'>
              <div className='card-header'>
                <div className='card-header-title'>
                  Add Items to the List
                </div>
              </div>
              <div className='card-content'>
                <div className='content'>
                  <p>How much elements add to page?</p>
                  <div className='field'>
                    <div className='control'>
                      {this.renderRadioButtons()}
                    </div>
                  </div>
                  <div className='field is-grouped'>
                    <div className='control'>
                      <button type='submit' className='button is-primary'>
                        Add
                      </button>
                    </div>
                    <div className='control'>
                      <button type='button' className='button is-text' onClick={this.handleCancel}>
                        Cancel
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </form>
        </Popup>
      )
    }
  }

  class RemoveForm extends Component {
    state = {
      selectedIndex: 0
    }

    handleChange = e => {
      this.setState({ selectedIndex: parseInt(e.target.value) });
    }

    handleSubmit = e => {
      e.preventDefault();
      this.props.onSubmit(this.state.selectedIndex);
      this.setState({ selectedIndex: 0 });
    }

    handleCancel = e => {
      this.props.onCancel();
      this.setState({ selectedIndex: 0 });
    }

    renderOptions () {
      return new Array(this.props.total).fill(null).map((el, i) => {
        return (
          <option value={i} key={i}>
            {i + 1}
          </option>
        )
      });
    }

    render () {
      return (
        <Popup isVisible={this.props.isVisible} onClose={this.handleCancel}>
          <form onSubmit={this.handleSubmit}>
            <div className='card'>
              <div className='card-header'>
                <div className='card-header-title'>
                  Remove an Item
                </div>
              </div>
              <div className='card-content'>
                <div className='content'>
                  <p>Select item to remove.</p>
                  <div className='field'>
                    <div className='control'>
                      <div className='select'>
                        <select value={this.state.selectedIndex} onChange={this.handleChange}>
                          {this.renderOptions()}
                        </select>
                      </div>
                    </div>
                  </div>
                  <div className='field is-grouped'>
                    <div className='control'>
                      <button type='submit' className='button is-danger'>
                        Remove
                      </button>
                    </div>
                    <div className='control'>
                      <button type='button' className='button is-text' onClick={this.handleCancel}>
                        Cancel
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </form>
        </Popup>
      )
    }
  }


  class App extends Component {
    state = {
      items: [],
      showPopup: null
    }

    onAddItems = count => {
      this.setState(state => ({
        showPopup: null,
        items: state.items.concat(api.fetchItems(count))
      }));
    }

    onRemoveItem = index => {
      this.setState(state => ({
        showPopup: null,
        items: state.items.filter((item, i) => i !== index)
      }));
    }

    onCancel = () => {
      this.setState({ showPopup: null });
    }

    onAddItemsButtonClick = e => {
      this.setState({ showPopup: 'ADD_FORM' });
    }

    onRemoveItemButtonClick = e => {
      this.setState({ showPopup: 'REMOVE_FORM' });
    }

    renderItems () {
      return this.state.items.map((item, i) => {
        return (
          <div key={i} className='column is-one-quarter'>
            <div className='card' style={{border: item.border}}>
              <div className='card-image'>
                <figure className='image'>
                  <img src={item.imageURL} />
                </figure>
              </div>
              <div className='card-content'>
                <div className='content'>
                  <h3>{item.title}</h3>
                </div>
              </div>
            </div>
          </div>
        );
      });
    }

    render () {
      return (
        <div>

          <section className="hero is-primary">
            <div className="hero-body">
              <div className="container">
                <h1 className="title">
                  Simple Function
                </h1>
                <h2 className="subtitle">
                  to manipulate array
                </h2>
              </div>
            </div>
          </section>

          <div className='section'>
            <div className='container'>
              <div className='field is-grouped'>
                <div className='control'>
                  <button className='button is-primary'
                          onClick={this.onAddItemsButtonClick}
                  >
                    Add items
                  </button>
                </div>
                <div className='control'>
                  <button className='button is-danger'
                          onClick={this.onRemoveItemButtonClick}
                          disabled={!this.state.items.length}
                  >
                    Remove items
                  </button>
                </div>
              </div>
            </div>
          </div>
          <div className='section'>
            <div className='container'>
              <div className='columns is-multiline'>
                {this.renderItems()}
              </div>
            </div>
          </div>
          <AddForm total={1}
                   isVisible={this.state.showPopup === 'ADD_FORM'}
                   onSubmit={this.onAddItems}
                   onCancel={this.onCancel}
          />
          <RemoveForm total={this.state.items.length}
                      isVisible={this.state.showPopup === 'REMOVE_FORM'}
                      onSubmit={this.onRemoveItem}
                      onCancel={this.onCancel}
          />
        </div>
      )
    }
  }

  render(
    <App />,
    document.body.appendChild(document.createElement('div'))
  );

}
